import React, { useState, useEffect } from 'react';


function Card() {

  var current_score = 0
  var current_questions_answered = 0

  const [questions, setQuestions] = useState(0);

  useEffect(() => {
    const fetchQuestions = async () => {const res = await fetch("http://bandersnatch-api.herokuapp.com/quizzes")
    const data = await res.json()
    setQuestions(data)
    console.log(data)
   }
  fetchQuestions()

  },[])

  const fetchScore = async () => {const res = await fetch("http://bandersnatch-api.herokuapp.com/users/1")
  const data = await res.json()
  current_score = data[0].score
  current_questions_answered = data[0].questions_answered
  console.log(current_score)
  console.log(current_questions_answered)
}
fetchScore()

  function updateQuestions() {
    let newArray = questions.slice(1)

    setQuestions(newArray)
    console.log(newArray[0])
  }

  // useEffect((updateQuestions) => {
  //   updateQuestions()
  //   }, [questions])




  return questions.length > 0 ? (
    <div className="block mx-auto px-6 	max-width: 768px bg-purple-300">

<div className="cardContainer p-6 mx-auto">

  <div className="bg-white text-pink-300 p-10 rounded-lg shadow-md">
    <h2 className="text-2-xl font-bold ">
      {questions[0].question}
    </h2>
  </div>

  <div className="grid grid-cols-2 gap-6 mt-6">

    <button onClick={updateQuestions} className="bg-white p-4
    text-pink-300 font-semibold rounded shadow">
    {questions[0].answer1}
    </button>
    <button className="bg-white p-4
    text-pink-300 font-semibold rounded shadow">
    {questions[0].answer2}
    </button>
    <button className="bg-white p-4
    text-pink-300 font-semibold rounded shadow">
    {questions[0].answer3}
    </button>
    <button className="bg-white p-4
    text-pink-300 font-semibold rounded shadow">
    {questions[0].answer4}
    </button>
  </div>
</div>
</div>
  ) : (
    <h2 className="text-2xl text-white -font-bold">Loading... Hold tight!</h2>
  )
  }


export default Card
